import React, { useState, useEffect } from 'react';
import './style.css';
import { CopyToClipboard } from 'react-copy-to-clipboard';


const DataDisplay = ({ urlData }) => {

    const [isCopied, setCopied] = useState(['']);
    const [isActive, setIsActive] = useState(false);

    useEffect(() => {
        const initialButtonsClickedText = urlData.map(() => 'Copy');
        setCopied(initialButtonsClickedText);
        const initialButtonsClickedStyle = urlData.map(() => false);
        setIsActive(initialButtonsClickedStyle);
    }, []);

    const handleClick = index => {
        setCopied(prev => 
            prev.map((item, buttonIndex) => (buttonIndex === index ? 'Copied!' : item))
        )
        setIsActive(prev => 
            prev.map((item, buttonIndex) => (buttonIndex === index ? true : item))
        )
    }


    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} className='newUrl'>{ url.result.full_short_link }</p>
                    <CopyToClipboard text={url.result.full_short_link}>
                        <button
                            key={index}
                            type="button"
                            className={isCopied[index] === 'Copy' ? 'copyBtn' : 'copiedBtn'}

                            onClick={() => handleClick(index)}
                        ><span>
                            {isCopied[index]}
                        </span>
                        </button>
                    </CopyToClipboard>
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
