import React, { useState, useEffect } from 'react';
import './style.css';
import { CopyToClipboard } from 'react-copy-to-clipboard';


const DataDisplay = ({ copyStatus, urlData }) => {

    // For updating whether or not the user has clicked the copy button
    const [isCopied, setCopied] = useState(copyStatus);

    useEffect(() => {
        const initialButtonsClickedText = copyStatus.map(() => 'Copy');
        setCopied(initialButtonsClickedText);
    }, []);

    // handles the click event of the Copy button. Changes the text value of that specific item to 'Copied'
    const handleClick = index => {

        const newArr=copyStatus.map((item, buttonIndex) => (buttonIndex === index ? 'Copied!' : item))
        setCopied(newArr);

    }

    useEffect(() => {
        const json = localStorage.getItem('urlData');
        const savedUrlData = JSON.parse(json);
        if (savedUrlData) {
            setUrlData(savedUrlData);
        }
    },[]);


    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} className='newUrl'>{ url.result.full_short_link }</p>
                    <CopyToClipboard text={url.result.full_short_link}>
                        <button
                            key={index}
                            type="button"
                            className='copyBtn'
                            className={isCopied[index] === 'Copy' ? 'copyBtn' : 'copiedBtn'}
                            onClick={() => handleClick(index)}
                        ><span>
                            {isCopied[index]}
                        </span>
                        </button>
                    </CopyToClipboard>
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
