import React, { useState } from 'react';
import './style.css';


const DataDisplay = ({ urlData }) => {

const { full_short_link, original_link } = urlData;


    return (
        <>
        <div className='resultContainer'>
            <div className="ResultRow">
                <div className="origUrlCol">
                    <p className='origUrl'>{ original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p className='newUrl'></p>
                    <div className='btnWrap'>
                        <button
                            type="submit"
                            className="copyBtn"
                            //will need to change the on click to target the short link, not "this"
                            onClick={() => {navigator.clipboard.writeText(this.state.textToCopy)}}
                        >
                            Copy
                        </button>
                    </div>
                </div>
            </div>
        </div>    
        </>
    )
}

export default DataDisplay
