import React, { useState, useRef } from 'react';
import { copy } from 'copy-to-clipboard';
import './style.css';


const DataDisplay = ({ urlData }) => {

    const [isCopied, setCopied] = useState(false);

    console.log('URL DATA'+urlData);

    // const CopyLink = () => {
    //     copy(this.state.url.result.full_short_link)
    //     setCopied(true);
    // }
    const url = useRef(null);

    function copyLink(e){
        url.current.select();
        document.execCommand('copy');
        e.target.focus();
        setCopied(true);
    };

    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} ref={url} className='newUrl'>{ url.result.full_short_link }</p>
                    {!isCopied && (
                    <button
                        type="button"
                        className="copyBtn"
                        onClick={copyLink}
                    ><span>
                        Copy
                    </span>
                    </button>
                    )}
                    {isCopied && (
                    <button
                        type="button"
                        className="copyBtn"
                        onClick={CopyToClipboard}
                        // onClick={CopyLink}
                    >
                        Copied!
                    </button>
                    )}
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
