import React, { useState } from 'react';
import './style.css';
import axios from 'axios';
import DataDisplay from './DataDisplay';

const Form = () => {
    const api = 'https://api.shrtco.de/v2/shorten?url=';
    var DataArray = [];

    // Stores/handles the entered URL form user
    const [inputUrl, setInputUrl] = useState('');
    // Stores/handles the fetched URL data
    const [urlData, setUrlData] = useState(DataArray);
    // Error message for if the user did not input any URL
    const [errMsg, setErrMsg] = useState(null);
    // Error message for if the user entered a bad URL
    const [badUrlMsg, setBadUrlMsg] = useState(null);
    // Success message for valid URL
    const [successMsg, setSuccessMsg] = useState(null);

    const [copyBtn, setCopyBtn] = useState('')


    // Handles capturing the url entered by the user in the input field and assiging it to inputURL
    function handleChange(e) {
        e.preventDefault();
        setInputUrl(e.target.value);
    }


    // Fetches the API data using the inputted URL
    const handleURL = () => {
        return axios.get(api+`${inputUrl}`)
              .then(res => {
                  console.log(res.data);
                  setUrlData([...urlData, res.data])
                  console.log(urlData)
                  setInputUrl('')
            }) .catch (err => {
                console.error("Error fetching data")
                console.log(err)
            })
        }

    // Handles the submition of the url and checks if it is valid, empty, or valid.
    function handleSubmit(e) {
        e.preventDefault();
        var regex = /(http|https):\/\/(\w+:{0,1}\w*)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%!\-\/]))?/;

        if (inputUrl === '') {
            setSuccessMsg(false);
            setBadUrlMsg(false);
            setErrMsg(true);
            setInputUrl('');
        } else if (!regex.test(inputUrl)) {
            setSuccessMsg(false);
            setBadUrlMsg(true);
            setErrMsg(false);
            setInputUrl('');
        } else {
            setInputUrl('');
            setBadUrlMsg(false);
            setErrMsg(false);
            setSuccessMsg(true);
            handleURL();
        }
    }



    return (
        <>
            <div className="sectionContainer">
                <div className='formContainer'>
                    <div className='formWrap'>
                        <form className="Form" onSubmit={handleSubmit}>
                            <div className="FormGroup form-group">
                                <input 
                                    type="text" 
                                    onChange={handleChange} 
                                    value={inputUrl} 
                                    className="Input" 
                                    size='80' 
                                    placeholder="Shorten a link here..."
                                    style={badUrlMsg || errMsg ? {border:'1.5px solid hsl(0, 87%, 67%)'} : {}}
                                />
                                {badUrlMsg && (
                                <span><p className='ErrorMsg'><i>Please add a valid link</i></p></span>
                                )}
                                {errMsg && (
                                <span><p className='ErrorMsg'><i>Please add a link</i></p></span>
                                )}
                                {successMsg && (
                                <span><p className='SuccessMsg'><i>Success!</i></p></span>
                                )}
                            </div>
                            <button
                                type="submit"
                                className="btn"
                            >
                                Shorten It!
                            </button>
                        </form>
                    </div> 
                    <div className='DisplayList'>
                        {urlData && (
                        <DataDisplay urlData = {urlData} />
                        )}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Form
