import React, { useState } from 'react';
import { copy } from 'copy-to-clipboard';
import './style.css';


const DataDisplay = ({ urlData }) => {

    var origLink = urlData.result.original_link;
    var shortLink = urlData.result.full_short_link;

    const [isCopied, setCopied] = useState(false);


    const CopyLink = () => {
        copy(shortLink)
        setCopied(true);
    }

    return (
        <>
        <div className='resultContainer'>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p className='origUrl'>{ urlData.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p className='newUrl'>{ urlData.result.full_short_link }</p>
                    {!isCopied && (
                    <button
                        type="submit"
                        className="copyBtn"
                        //will need to change the on click to target the short link, not "this"
                        // onClick={}
                    ><span>
                        Copy
                        </span>
                    </button>
                    )}
                    {isCopied && (
                    <button
                        type="submit"
                        className="copyBtn"
                        onClick={CopyLink}
                        //will need to change the on click to target the short link, not "this"
                        // onClick={}
                    >
                        Copied!
                    </button>
                    )}
                </div>
            </div>
        </div>   
        </>
    )
}

export default DataDisplay
