import React, { useState, useRef } from 'react';
import { copy } from 'copy-to-clipboard';
import './style.css';


const DataDisplay = ({ urlData }) => {

    const [isCopied, setCopied] = useState(false);

    console.log('URL DATA'+urlData);

    const CopyLink = () => {
        copy(this.state.url.result.full_short_link)
        setCopied(true);
    }

    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} className='newUrl'>{ url.result.full_short_link }</p>
                    {!isCopied && (
                    <button
                        type="button"
                        className="copyBtn"
                        onClick={CopyLink}
                    ><span>
                        Copy
                    </span>
                    </button>
                    )}
                    {isCopied && (
                    <button
                        type="button"
                        className="copyBtn"
                        onClick={CopyLink}
                        // onClick={CopyLink}
                    >
                        Copied!
                    </button>
                    )}
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
