import React, { useState, useEffect } from 'react';
import './style.css';
import { CopyToClipboard } from 'react-copy-to-clipboard';


const DataDisplay = ({ copyStatus, urlData }) => {

    // const copyArray = ['Copy', 'Copy', 'Copy']
    console.log(copyStatus[index])

    // const [copyStyle, setCopyStyle] = useState(copyBtnStyle);
    const [isCopied, setCopied] = useState(copyStatus);
    const [isActive, setIsActive] = useState(false);

    useEffect(() => {
        const initialButtonsClickedText = copyStatus.map(() => 'Copy');
        setCopied(initialButtonsClickedText);
        const initialButtonsClickedStyle = urlData.map(() => false);
        setIsActive(initialButtonsClickedStyle);
    }, []);

    const handleClick = index => {
        // setCopied(prev => 
        //     prev.map((item, buttonIndex) => (buttonIndex === index ? 'Copied!' : item))
        // )
        // a.forEach(function(item, i) { if (item == 3452) a[i] = 1010; });
        // copyStatus.forEach(function(item, buttonIndex) { if (buttonIndex == index) copyStatus[index] = 'Copied'; });

        const newArr=copyStatus.map((item, buttonIndex) => (buttonIndex === index ? 'Copied!' : item))
        console.log(newArr);
        setCopied(newArr);

        const itemReplacer = (copyStatus, oldItem, newItem) =>
            copyStatus.map(buttonIndex => index === oldItem ? newItem : item)


        setIsActive(prev => 
            prev.map((item, buttonIndex) => (buttonIndex === index ? true : item))
        )
    }


    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} className='newUrl'>{ url.result.full_short_link }</p>
                    <CopyToClipboard text={url.result.full_short_link}>
                        <button
                            key={index}
                            type="button"
                            className='copyBtn'
                            // className={copyStyle[index] === false ? 'copyBtn' : 'copiedBtn'}
                            onClick={() => handleClick(index)}
                        ><span>
                            {isCopied[index]}
                        </span>
                        </button>
                    </CopyToClipboard>
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
