import React, {useState, useEffect} from 'react';
import './style.css';
import BgImg from '../../images/bg-boost-desktop.svg';
import axios from 'axios';

const Form = () => {
    const api = 'https://api.shrtco.de/v2/shorten?url=';
    var inputExists = true;
    var goodInput = true;

    // Stores/handles the entered URL form user
    const [inputUrl, setInputUrl] = useState('');
    // Stores/handles the fetched URL data
    const [urlData, setUrlData] = useState(null);
    // Error message for if the user did not input any URL
    const [errMsg, setErrMsg] = useState(null);
    // Error message for if the user entered a bad URL
    const [badUrlMsg, setBadUrlMsg] = useState(null);

    // Checking to see if either there was no URL entered or the URL is bad
    // returning corresponding error messages
    function checkInput(e) {
        if (e.target.value === ''){
            goodInput = true;
            inputExists = false;

            setBadUrlMsg(false)
            setErrMsg(true);
        } else {
            inputExists = true;
            setErrMsg(false);
        }
    }

    // Need to figure out how to check if a URL is bad/faulty - create a function that will handle the appropriate error


    // Handles capturing the url entered by the user in the input field and assiging it to inputURL
    function handleChange(e) {
        setInputUrl(e.target.value);
    }


    // Fetches the API data using the inputted URL
    const handleURL = () => {
        return axios.get(api+`${inputUrl}`)
              .then(res => {
                  console.log(res.data);
                  setUrlData(res.data)
                  setInputUrl(res.data)
            }) .catch (error => {
                console.error("Error fetching data")
                console.log(error)
            })
        }

    function handleSubmit(e) {
        e.preventDefault();

        if (inputUrl === '') {
            goodInput = true;
            inputExists = false;

            setBadUrlMsg(false)
            setErrMsg(true);
        } else {
            inputExists = true;
            setErrMsg(false);
        }
    }

    function ValidURL(str) {
        var regex = /(http|https):\/\/(\w+:{0,1}\w*)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%!\-\/]))?/;
        if(!regex .test(str)) {
          alert("Please enter valid URL.");
          return false;
        } else {
          return true;
        }
      }


    // Submitting the form and resetting the input field to be blank.
    function handleSubmit(e) {
        e.preventDefault();

        if (inputExists === true && goodInput === true) {
            setInputUrl('');
            handleURL();
        } else {
            checkInput();
        }
    }

    function checkInput(e) {
        if (e.target.value === ''){
            goodInput = true;
            inputExists = false;

            setBadUrlMsg(false)
            setErrMsg(true);
        } else {
            inputExists = true;
            setErrMsg(false);
        }
    }



    return (
        <>
            <div className='formContainer'>
                <div className='bgImg'>
                    <img className='BG' src={BgImg} type='image/svg'/>
                </div>
                <form className="Form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <input 
                            type="text" 
                            onChange={handleChange} 
                            value={inputUrl} 
                            className="Input" 
                            size='80' 
                            // onBlur={checkInput}
                            placeholder="Shorten a link here..."
                        />
                        {badUrlMsg && (
                        <p className='ErrorMsg'><i>Please add a valid link to shorten</i></p>
                        )}
                        {errMsg && (
                        <p className='ErrorMsg'><i>Please add a link</i></p>
                        )}
                    </div>
                    <button
                        type="submit"
                        className="btn"
                    >
                        Shorten It!
                    </button>
                </form>
            </div>
        </>
    )
}

export default Form


