import React, { useState, useRef } from 'react';
import { copy } from 'copy-to-clipboard';
import './style.css';
import { CopyToClipboard } from 'react-copy-to-clipboard';


const DataDisplay = ({ urlData }) => {

    const [isCopiedText, setCopied] = useState('Copy');


    const CopyLink = () => {
        setCopied('Copied!');
    }

    // might be able to change the text of the button with the onclick as opposed
    // to two separate buttons

    return (
        <>     
        <div className='resultContainer'>
            {urlData.map((url, index) =>
            <div className="resultRow">
                <div className="origUrlCol">
                    <p key={index} className='origUrl'>{ url.result.original_link }</p>
                </div>
                <div className='newUrlCol'>
                    <p key={index} className='newUrl'>{ url.result.full_short_link }</p>
                    {/* {!isCopied && ( */}
                    <CopyToClipboard text={url.result.full_short_link}>
                        <button
                            type="button"
                            className="copyBtn"
                            onClick={CopyLink}
                        ><span>
                            {isCopiedText}
                        </span>
                        </button>
                    </CopyToClipboard>
                    // )}
                    {/* {isCopied && (
                    <button
                        type="button"
                        className="copyBtn"
                    >
                        Copied!
                    </button>
                    )} */}
                </div>
            </div>
            )}
        </div>  
        </>
    )
}

export default DataDisplay
