import React, { useState } from 'react';
import './style.css';
import axios from 'axios';
import DataDisplay from './DataDisplay';

const Form = () => {
    const api = 'https://api.shrtco.de/v2/shorten?url=';

    const style = {
        border: '1px solid hsl(0, 87%, 67%)', 
   };

    // Stores/handles the entered URL form user
    const [inputUrl, setInputUrl] = useState('');
    // Stores/handles the fetched URL data
    const [urlData, setUrlData] = useState(null);
    // Error message for if the user did not input any URL
    const [errMsg, setErrMsg] = useState(null);
    // Error message for if the user entered a bad URL
    const [badUrlMsg, setBadUrlMsg] = useState(null);
    // Success message for valid URL
    const [successMsg, setSuccessMsg] = useState(null);


    // Handles capturing the url entered by the user in the input field and assiging it to inputURL
    function handleChange(e) {
        setInputUrl(e.target.value);
    }


    // Fetches the API data using the inputted URL
    const handleURL = () => {
        return axios.get(api+`${inputUrl}`)
              .then(res => {
                  console.log(res.data);
                  setUrlData(res.data)
                  setInputUrl(res.data)
            }) .catch (err => {
                console.error("Error fetching data")
                console.log(err)
            })
        }

    // Handles the submition of the url and checks if it is valid, empty, or valid.
    function handleSubmit(e) {
        e.preventDefault();
        var regex = /(http|https):\/\/(\w+:{0,1}\w*)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%!\-\/]))?/;

        if (inputUrl === '') {
            setSuccessMsg(false);
            setBadUrlMsg(false);
            setErrMsg(true);
        } else if (!regex.test(inputUrl)) {
            setSuccessMsg(false);
            setBadUrlMsg(true);
            setInputUrl('');
        } else {
            setInputUrl('');
            setBadUrlMsg(false);
            setErrMsg(false);
            setSuccessMsg(true);
            handleURL();
        }
    }



    return (
        <>
            <div className="sectionContainer">
                <div className='formContainer'>
                    <div className='formWrap'>
                        <form className="Form" onSubmit={handleSubmit}>
                            <div className="FormGroup form-group">
                                <input 
                                    type="text" 
                                    onChange={handleChange} 
                                    value={inputUrl} 
                                    className="Input" 
                                    size='80' 
                                    placeholder="Shorten a link here..."
                                    style={badUrlMsg || errMsg ? 'border':'1px solid hsl(0, 87%, 67%)'}
                                />
                                {badUrlMsg && (
                                <p className='ErrorMsg'><i>Please add a valid link to shorten</i></p>
                                )}
                                {errMsg && (
                                <p className='ErrorMsg'><i>Please add a link</i></p>
                                )}
                                {successMsg && (
                                <p className='SuccessMsg'><i>Success!</i></p>
                                )}
                            </div>
                            <button
                                type="submit"
                                className="btn"
                            >
                                Shorten It!
                            </button>
                        </form>
                    </div> 
                    {urlData && (
                <DataDisplay urlData = {urlData} />
                )}
                </div>
                {/* {urlData && (
                <DataDisplay urlData = {urlData} />
                )} */}
            </div>
        </>
    )
}

export default Form
